CREATE TABLE POSITION_QUESTION (
                                   POSITION_QUESTION_ID INT AUTO_INCREMENT PRIMARY KEY,
                                   POSITION_ID INT,
                                   QUESTION_ID INT,
                                   QUESTION_ORDER INT,
                                   FOREIGN KEY (`POSITION_ID`) REFERENCES `POSITION`(`POSITION_ID`),
                                   FOREIGN KEY (`QUESTION_ID`) REFERENCES `QUESTION`(`QUESTION_ID`)
);
ALTER TABLE POSITION_QUESTION AUTO_INCREMENT=1001;
